sys_script_include.script:
var verifyRoomAvailability = Class.create();
verifyRoomAvailability.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
    getUnavailableRooms: function() {
        var grRooms = GlideRecord('x_423377_californi_room_availability');
        grRooms.addQuery('availability', false);
        grRooms.query();
		var ret = '';
		while (grRooms.next()) {
			ret += grRooms.number + ';';
		}
		if (ret)
			ret = ret.substr(0, ret.length -1);
		return ret;
    },
    type: 'verifyRoomAvailability'
});
